definition.script:
pipeline {
    agent any 
    stages {
        stage('Git') {
            steps {
                node('master'){
                    sh '''
                    rm -rf *
                    git clone -b task7 git@github.com:yu74co/devopstraining.git
                    cd devopstraining
                    git add *
                    git config user.email "sem.y@tut.by"
                    git config user.name "Yury Semchanka"
                    git config push.default simple
                    '''
                }
            }
        }

        stage('Gradle'){
            steps {
                node('master'){
                    sh '''chmod -R 755 devopstraining
                        cd devopstraining
                        ./gradlew incrementVersion
                        ./gradlew build
                    '''
                    script { 
                        VERs = readFile('./devopstraining/gradle.properties')
                        String version=VERs.drop(VERs.lastIndexOf('=')+1)
                        echo "${version}"
                        env.ver = "${version}"
                    }
                    sh 'curl -XPUT -u admin:admin123 -T ./devopstraining/build/libs/devopstraining-$ver.war http://server1:8081/nexus/content/repositories/snapshots/task7/$ver/test.war'
                }

            }
        }

        stage('Docker build and publish') {
            steps {
                node('master'){
                    sh '''
                        echo $ver
                        cd ./devopstraining
                        #sudo docker run -d -p 5000:5000 --name registry registry:2
                        sudo docker build --build-arg version=$ver --tag=mytomcat .
                        sudo docker image tag mytomcat localhost:5000/task7:$ver
                        sudo docker push localhost:5000/task7:$ver
                        if [ -z $(sudo docker service ls | grep myserv) ]; then
                            # sudo docker swarm init
                            sudo docker service create --replicas 2 --name myserv -p 4000:8080 localhost:5000/task7:$ver
                        else
                            sudo docker service update --image localhost:5000/task7:$ver myserv
                        fi

                        #sudo docker run -d -p 4000:8080 localhost:5000/task7:$ver
                        #sudo docker container stop registry && sudo docker container rm -v registry
                    '''
                }

            }
        }

        stage ('Check url') {
            steps {
                node ('master') {
                    script {
                        httpRequest acceptType: 'TEXT_HTML', consoleLogResponseBody: true, url: 'http://192.168.0.10:4000/test'
                    }    
                }
            }
        }


        stage ('Git push') {
            steps {
                node('master'){
                    sh '''
                    cd devopstraining
                    git add ./gradle.properties
                    git status
                    git commit -a -m "Update version"
                    git push
                    git checkout master
                    git merge task7
                    git push
                    git tag -a $ver -m 'version'
                    git push origin $ver
                    '''
                }
            }
        }

    }
}
