definition.script:
pipeline {
    agent any 
    stages {
        stage('Git') {
            steps {
                node('master'){
                    sh '''
                    rm -rf *
                    git clone -b task6 git@github.com:yu74co/devopstraining.git
                    cd devopstraining
                    git add *
                    git config user.email "sem.y@tut.by"
                    git config user.name "Yury Semchanka"
                    git config push.default simple
                    '''
                }
            }
        }

        stage('Gradle'){
            steps {
                node('master'){
                    sh '''chmod -R 755 devopstraining
                        cd devopstraining
                        ./gradlew incrementVersion
                        ./gradlew build
                        export ver=$(cat gradle.properties | grep version= | sed 's/version=//')
                        #export $ver
                        curl -XPUT -u admin:admin123 -T build/libs/devopstraining-$ver.war http://server1:8081/nexus/content/repositories/snapshots/task6/$ver/test.war
                        echo $ver > ../../ver.tmp
                    '''
                    script {
                    VERs = readFile('../ver.tmp')
                    }
                }

            }
        }

        stage('Deploy1') {
            steps {
                echo "${VERs}"
                node('tomcat1'){
                    sh 'rm -rf *'
                    writeFile file: "ver.tmp", text: "${VERs}"

                    sh '''
                        ver=$(cat ver.tmp)
                        wget http://server1:8081/nexus/content/repositories/snapshots/task6/$ver/test.war
                        curl http://server1/jkmanager?cmd=update&from=list&w=lb&sw=server2&vwa=1
                        cp test.war /var/lib/tomcat/webapps
                        sleep 5s
                        curl http://server2:8080/test/index.jsp
                        curl http://server1/jkmanager?cmd=update&from=list&w=lb&sw=server2&vwa=0
                    '''
                }
            }
        }

        stage('Deploy2') {
            steps {
                echo "${VERs}"
                node('tomcat2'){
                    sh 'rm -rf *'
                    writeFile file: "ver.tmp", text: "${VERs}"

                    sh '''
                        ver=$(cat ver.tmp)
                        wget http://server1:8081/nexus/content/repositories/snapshots/task6/$ver/test.war
                        curl http://server1/jkmanager?cmd=update&from=list&w=lb&sw=server3&vwa=1
                        cp test.war /var/lib/tomcat/webapps
                        sleep 5s
                        curl http://server3:8080/test/index.jsp
                        curl http://server1/jkmanager?cmd=update&from=list&w=lb&sw=server3&vwa=0
                    '''
                }
            }
        }

        stage ('Git push') {
            steps {
                node('master'){
                    sh '''
                    cd devopstraining
                    git add ./gradle.properties
                    git status
                    git commit -a -m "Update version"
                    git push
                    git checkout master
                    git merge task6
                    git push
                    ver=$(cat ../../ver.tmp)
                    git tag -a $ver -m 'version'
                    git push origin $ver
                    '''
                }
            }
        }
    }
}
